<template>
  <b-btn
    v-fmv-toggle.settings
    :class="buttonClass"
    :variant="variant">
    <md-icon>settings</md-icon>
  </b-btn>
</template>

<style lang="scss" scoped>
.app-settings-button {
  position: fixed;
  top: 100px;

  &--right {
    right: 0/*! rtl:ignore */;
    border-top-right-radius: 0/*! rtl:ignore */;
    border-bottom-right-radius: 0/*! rtl:ignore */;
  }
  &--left {
    left: 0/*! rtl:ignore */;
    border-top-left-radius: 0/*! rtl:ignore */;
    border-bottom-left-radius: 0/*! rtl:ignore */;
  }
}
</style>

<script>
import MdIcon from '../MdIcon.vue'
export default {
  components: {
    MdIcon
  },
  props: {
    align: {
      type: String,
      default: 'right'
    },
    variant: {
      type: String,
      default: 'dark'
    }
  },
  computed: {
    localAlign() {
      if (this.mainDrawerAlign) {
        return this.mainDrawerAlign === 'end' ? 'left' : 'right'
      }
      return this.align
    },
    buttonClass() {
      return ['app-settings-button', `app-settings-button--${this.localAlign}`]
    }
  }
}
</script>
