<template>
  <fmv-drawer 
    id="settings" 
    :align="localAlign">
    <fmv-sidebar 
      :align="localAlign"
      :type="sidebarType"
      :variant="localSidebarVariant">
      <div class="p-2">

        <collapse
          v-for="option in options"
          v-if="option.visible !== false"
          :key="`collapse-${option.id}`"
          :id="option.id"
          :title="option.title">
          
          <b-form-group
            v-for="(group, index) in option.children"
            v-if="group.visible !== false"
            :key="`group-${option.id}-${index}`"
            :label="group.title"
            :class="{ 'mb-0': index === option.children.length - 1 }">

            <form-image-group
              v-if="group.component === 'form-image-group'"
              :options="group.options"
              v-model="settings[`${option.id}.${group.id}`]"
              :name="group.id" />

            <b-form-radio-group 
              v-if="group.component === 'b-form-radio-group'"
              :id="`group-${option.id}-${index}`"
              v-model="settings[`${option.id}.${group.id}`]"
              :options="group.options" />

            <b-form-checkbox
              v-if="group.component === 'b-form-checkbox'"
              :id="`group-${option.id}-${index}`"
              v-model="settings[`${option.id}.${group.id}`]">
              {{ group.title }}
            </b-form-checkbox>

            <b-form-checkbox
              v-if="group.component === 'custom-checkbox-toggle'"
              :id="`group-${option.id}-${index}`"
              v-model="settings[`${option.id}.${group.id}`]"
              class="custom-checkbox-toggle custom-control-inline mr-1">&nbsp;</b-form-checkbox>

            <b-form-select
              v-if="group.component === 'b-form-select'"
              :id="`group-${option.id}-${index}`"
              v-model="settings[`${option.id}.${group.id}`]"
              :options="group.options" />

            <label 
              v-if="group.component === 'custom-checkbox-toggle'"
              :for="`group-${option.id}-${index}`"
              class="mb-0">
              <span class="form-label m-0">{{ group.title }}</span>
            </label>

          </b-form-group>
        </collapse>

        <slot />

        <pre 
          v-if="debug" 
          v-text="settings" />

      </div>
    </fmv-sidebar>
  </fmv-drawer>
</template>

<style lang="scss">
@import 'ui-learnplus-bootstrap/scss/variables.scss';

#settings {
  .mdk-drawer__content {
    width: 300px;
  }
  .mdk-drawer__scrim {
    background-color: rgba($body-color, 0.5);
  }
  .col-form-label {
    font-size: 0.75rem;
    text-transform: uppercase;
    color: $text-muted;
    line-height: 1rem;
    font-weight: 500;
  }
}
</style>

<script>
import Settings from './Settings.vue'
import { FmvDrawer, FmvSidebar, utils } from 'fmv-layout'
const { drawerProps, sidebarProps, prefixProps } = utils

export default {
  components: {
    FmvDrawer,
    FmvSidebar
  },
  extends: Settings,
  props: {
    ...prefixProps(drawerProps, 'drawer'),
    ...prefixProps(sidebarProps, 'sidebar')
  },
  computed: {
    localAlign() {
      if (this.mainDrawerAlign) {
        return this.mainDrawerAlign === 'end' ? 'left' : 'right'
      }
      return this.drawerAlign
    },
    localSidebarVariant() {
      return this.sidebarVariant || 'sidebar-transparent-xs-up'
    }
  }
}
</script>
